// based on Zhell's macro https://github.com/krbz999/zhell-macros

function arcaneRecovery(actor, feature) {
  const levels = actor.getRollData().classes.wizard.levels;
  const spellConfig = foundry.utils.duplicate(actor.system.spells);
  const spellLevels = [];

  const arcaneGrimoire = actor.items.some((i) =>
    i.name.startsWith("Arcane Grimoire")
    && i.system?.equipped && i.system?.attunement === 2
  );

  for (let i = 1; i <= 5; i++) {
    if (spellConfig[`spell${i}`].max > 0) spellLevels.push(spellConfig[`spell${i}`]);
  }

  if (!spellLevels.some((level) => level.value !== level.max)) {
    ui.notifications.warn("You have no appropriate spell slots to recover");
    return;
  }

  const totalSpellLevels = Math.ceil(levels / 2) + (arcaneGrimoire ? 1 : 0);
  let spent = 0;

  const agText = arcaneGrimoire ? "<p><i>Bonus +1 slot from Arcane Grimoire</i></p>" : "";

  let spellSlotChoices = "";
  for (let i = 0; i < spellLevels.length; i++) {
    const val = i + 1;
    const name = `level${val}`;
    spellSlotChoices += `
    <div class="form-group">
      <label style="text-align:center"><strong>${val.ordinalString()}-level</strong></label>
      <div class="form-fields">`;
    for (let j = 0; j < spellLevels[i].max; j++) {
      const cd = j < spellLevels[i].value ? "checked disabled" : "";
      spellSlotChoices += `<input type="checkbox" value="${val}" name="${name}" ${cd}></input>`;
    }
    spellSlotChoices += "</div></div>";
  }
  let content = `<p name="header">Recovering spell slots: <strong>${spent}</strong> / ${totalSpellLevels}.</p>
${agText} <hr> <form>
${spellSlotChoices}
</form> <hr>`;

  const dialog = new Dialog({
    title: feature.name,
    content,
    buttons: {
      go: {
        icon: `<i class="fa-solid fa-hat-wizard"></i>`,
        label: "Recover",
        callback: async (html) => {
          if (spent > totalSpellLevels || spent < 1) {
            ui.notifications.warn("Invalid number of slots to recover.");
            return dialog.render(true);
          }
          for (let i = 0; i < 9; i++) {
            const selector = `input[name=level${i + 1}]:checked`;
            const val = html[0].querySelectorAll(selector).length;
            spellConfig[`spell${i + 1}`].value = val;
          }
          await actor.update({ "system.spells": spellConfig });
          ui.notifications.info("Spell slots recovered!");
        },
      },
    },
    render: (html) => {
      html[0].addEventListener("change", function () {
        const selector = "input:checked:not(:disabled)";
        const inputs = html[0].querySelectorAll(selector);
        spent = Array.from(inputs).reduce((acc, node) => {
          return acc + Number(node.value);
        }, 0);
        const hint = `Recovering spell slots: <strong>${spent}</strong> / ${totalSpellLevels}.`;
        html[0].querySelector("[name=header]").innerHTML = hint;
      });
    },
  }).render(true);

}

if (scope && foundry.utils.getProperty(scope, "flags.ddb-importer.ddbMacroFunction")) {
  if (!actor || ! item) {
    logger.error("No actor or item passed to arcane recovery");
    return;
  }
  arcaneRecovery(actor, item);
} else if (args && args[0] === "on") {
  const lastArg = args[args.length - 1];
  const tActor = await fromUuid(lastArg.actorUuid);
  const feature = await fromUuid(lastArg.origin);

  arcaneRecovery(tActor, feature);
}

async function sustainedDamage({ options, damageType, damageDice, sourceItem, caster }) {
  const damageRoll = await new CONFIG.Dice.DamageRoll(`${damageDice}[${damageType}]`).evaluate({ async: true });
  if (game.dice3d) game.dice3d.showForRoll(damageRoll, game.users.get(options.userId));

  // console.warn({ options, damageType, damageDice, sourceItem, caster });
  const targets = await Promise.all(options.targets.map(async (uuid) => {
    const tok = await fromUuid(uuid);
    return tok.object;
  }));
  const casterToken = await fromUuid(options.sourceUuid);
  const itemData = sourceItem.toObject();
  itemData.system.properties = DDBImporter?.EffectHelper.removeFromProperties(itemData.system.properties, "concentration") ?? [];
  itemData.effects = [];
  delete itemData._id;

  const workflow = await new MidiQOL.DamageOnlyWorkflow(
    caster,
    casterToken,
    damageRoll.total,
    damageType,
    targets,
    damageRoll,
    {
      flavor: `(${CONFIG.DND5E.damageTypes[damageType].label})`,
      itemCardId: "new",
      itemData,
      isCritical: false,
    }
  );
}

async function cancel(caster, options) {
  // Remove concentration and the effect causing it since the effect has been used
  const concentration = MidiQOL.getConcentrationEffect(caster);

  if (concentration) {
    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: caster.uuid, effects: [concentration.id] });
  }
  if (options.askerEffect) {
    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: caster.uuid, effects: [options.askerEffect] });
  }
  await DAE.unsetFlag(caster, "witchBoltSpell");
}

const lastArg = args[args.length - 1];
const damageDice = "1d12";
const damageType = "lightning";

if (args[0].macroPass === "postActiveEffects") {
  if (args[0].hitTargetUuids.length === 0) return {}; // did not hit anyone

  const effectData = [{
    label: "WitchBolt Ongoing",
    name: "WitchBolt Ongoing",
    img: args[0].item.img,
    duration: { rounds: 10, startTime: game.time.worldTime },
    origin: args[0].item.uuid,
    changes: [DDBImporter.lib.DDBMacros.generateMacroChange({ macroType: "spell", macroName: "witchBolt.js", document: { name: "Witch Bolt" } })],
    disabled: false,
    "flags.dae.macroRepeat": "startEveryTurn",
  }];

  const effect = await args[0].actor.createEmbeddedDocuments("ActiveEffect", effectData);

  const options = {
    targets: args[0].hitTargetUuids,
    sourceUuid: args[0].tokenUuid,
    distance: args[0].item.system.range.value,
    userId: game.userId,
    askerEffect: effect[0]._id,
  };

  await DAE.setFlag(args[0].actor, "witchBoltSpell", options);

  // console.warn("WitchBolt", {options, effectData, args, actor: args[0].actor, effect});

} else if (args[0] == "off") {
  const sourceItem = await fromUuid(lastArg.origin);
  const caster = sourceItem.parent;
  DAE.unsetFlag(caster, "witchBoltSpell");
} else if (args[0] == "each") {
  const sourceItem = await fromUuid(lastArg.origin);
  const caster = sourceItem.parent;
  const options = DAE.getFlag(caster, "witchBoltSpell");
  // console.warn("on each", {
  //   options,
  //   sourceItem,
  //   lastArg,
  //   caster,
  // })
  const isInRange = await DDBImporter?.EffectHelper.checkTargetInRange(options);
  if (isInRange) {
    const userIds = Object.entries(caster.ownership).filter((k) => k[1] === CONST.DOCUMENT_OWNERSHIP_LEVELS.OWNER).map((k) => k[0]);
    await ChatMessage.create({
      content: `<p>${caster.name} may use their action to sustain Witch Bolt. Asking them now...</p><br>`,
      type: CONST.CHAT_MESSAGE_TYPES.OTHER,
      speaker: caster.uuid,
      whisper: game.users.filter((u) => userIds.includes(u.id) || u.isGM),
    });
    const result = await DDBImporter.DialogHelper.AskUserButtonDialog(options.userId, {
        buttons: [
          { label: "Yes, damage!", value: true},
          { label: "No, end concentration", value: false }
        ],
        title: "Witch Bolt",
        content: "<p>Use action to sustain Witch Bolt?</p>"
      },
      'column');
    // console.warn("result", result);
    if (result) {
      sustainedDamage({options, damageType, damageDice, sourceItem, caster} );
    } else {
      cancel(caster, options);
    }
  }
}

let lightSource = "torch";
let file = "modules/gambits-premades/assets/images/torch.webp";
let torchStatus = Sequencer.EffectManager.getEffects({ name: `${token.document.id} ${lightSource}` });

async function updateTorch() {
    if (args[0].macroPass === "preApplyDynamicEffects") {
        if (torchStatus.length === 0 && (macroItem.system.uses.value !== 0 || macroItem.system.quantity !== 1)) {
            await showTorchLightDialog();
        } else if (torchStatus.length !== 0) {
            await showTorchOptionsDialog();
        }
        else {
            ui.notifications.warn("You do not have any torches remaining.")
            workflow.aborted = true;
        }
    }

    if (args[0] === "off" && args[1]["expiry-reason"] && (args[1]["expiry-reason"].includes("times-up") || args[1]["expiry-reason"].includes("effect-deleted")) && args[1]?.existing !== "effect-stacking") {
        const light = { dim: 0, bright: 0 };
        await token.document.update({ light });
        await Sequencer.EffectManager.endEffects({ name: `${token.document.id} ${lightSource}`, object: token });
    }
}

