async function lightNewTorch() {
 new Sequence()

        .effect()
        .delay(150)
        .name(`${token.document.id} ${lightSource}`)
        .file(game.modules.get("jb2a_patreon")?.active ? "jb2a.impact.002.orange" : "") //Add fallback effect path here in the empty quotes if there's another you'd like to use due to no patreon jb2a
        .atLocation(token)
        .attachTo(token, {followRotation: true, local: true})
        .scaleToObject(0.9, {considerTokenScale: true})
        .spriteOffset({x:0.525*token.document.width, y:-0.05*token.document.width}, {gridUnits:true})
        .spriteRotation(45)
        .zIndex(0.1)

        .effect()
        .name(`${token.document.id} ${lightSource}`)
        .file(file)
        .atLocation(token)
        .attachTo(token, {followRotation: true, local: true})
        .scaleToObject(1.2, {considerTokenScale: true})
        .scaleIn(0, 500, {ease: "easeOutElastic"})
        .scaleOut(0, 250, {ease: "easeOutCubic"})
        .spriteOffset({x:0.35*token.document.width, y:0.1*token.document.width}, {gridUnits:true})
        .animateProperty("sprite", "rotation", { from: 60, to: -60, duration: 300, ease: "easeInOutBack"})
        .animateProperty("sprite", "rotation", { from: 0, to: 30, duration: 250, delay: 200, ease: "easeOutBack"})
        .persist()
        .zeroSpriteRotation()

        .effect()
        .delay(250)
        .name(`${token.document.id} ${lightSource}`)
        .file("jb2a.flames.01.orange")
        .atLocation(token)
        .attachTo(token, {followRotation: true, local: true})
        .scaleToObject(1, {considerTokenScale: true})
        .spriteOffset({x:0.5*token.document.width, y:-0.05*token.document.width}, {gridUnits:true})
        .persist()
        .spriteRotation(45)
        .zIndex(0.1)
        .waitUntilFinished(-500)

        .play()

    const light = { dim: 40, bright: 20, alpha: 0.25, angle:360, luminosity: 0.5, color: "#ffb433", animation: { type: "lantern", speed: 4, intensity: 4 }, attenuation: 0.75, contrast: 0.15, shadows: 0.2 };
    await token.document.update({ light });
    if (macroItem.system.quantity > 1) {
        await macroItem.update({ "system.quantity": macroItem.system.quantity - 1 });
    } else {
        //await actor.deleteEmbeddedDocuments("Item", [macroItem.id]);
        await macroItem.update({ "system.uses.value": 0 });
    }
}

