data.command:
async function wait(ms) { return new Promise(resolve => { setTimeout(resolve, ms); }); }
const lastArg = args[args.length - 1];
if (lastArg.hitTargets.length === 0) return {};
const actorD = game.actors.get(lastArg.actor._id);
const tokenD = canvas.tokens.get(lastArg.tokenId);
const target = canvas.tokens.get(lastArg.hitTargets[0].id);
const itemD = lastArg.item;
const damageType = "bludgeoning";
const damageRoll = new Roll(`1d8 + @abilities.str.mod`, actorD.getRollData()).evaluate({ async: false });
const effect = target.actor.effects.find(ef => ef.data.label === game.i18n.localize("Grappled"));
if (!effect) {
    new Dialog({
        title: itemD.name,
        content: `Pick an attack`,
        buttons: {
            attack: {
                label: "Attack", callback: async () => {
                    game.dice3d?.showForRoll(damageRoll);
                    await new MidiQOL.DamageOnlyWorkflow(actorD, tokenD, damageRoll.total, damageType, [target], damageRoll, { flavor: `(${CONFIG.DND5E.damageTypes[damageType]})`, itemCardId: lastArg.itemCardId, useOther: false });
                }
            },
            grapple: {
                label: "Grappled", callback: async () => {
                    let saveType = "dex";
                    let DC = 18;
                    let save = await target.actor.rollAbilitySave(saveType, { chatMessage: false, fastForward: true });
                    let success = "saves";
                    if (save.total < DC) {
                        success = "fails";
                        let gameRound = game.combat ? game.combat.round : 0;
                        let effectData = {
                            label: "Grappled",
                            icon: "modules/combat-utility-belt/icons/grappled.svg",
                            origin: lastArg.uuid,
                            disabled: false,
                            duration: { rounds: 10, seconds: 60, startRound: gameRound, startTime: game.time.worldTime },
                            changes: [{ key: `data.attributes.movement.all`, mode: 5, value: 0, priority: 20 }]
                        };
                        await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: target.uuid, effects: [effectData] });
                    }
                    await wait(300);
                    let grapple_msg = `<div class="midi-qol-flex-container"><div class="midi-qol-target-npc midi-qol-target-name" id="${target.id}">${target.name} ${success} with ${save.total}</div><div><img src="${target.data.img}" width="30" height="30" style="border:0px"></div></div>`;
                    let grapple_result = `<div class="midi-qol-nobox midi-qol-bigger-text">Grapple ${CONFIG.DND5E.abilities[saveType]} Saving Throw: DC ${DC}</div><div><div class="midi-qol-nobox">${grapple_msg}</div></div>`;
                    let chatMessage = await game.messages.get(args[0].itemCardId);
                    let content = await duplicate(chatMessage.data.content);
                    let searchString = /<div class="midi-qol-hits-display">[\s\S]*<div class="end-midi-qol-hits-display">/g;
                    let replaceString = `<div class="midi-qol-hits-display"><div class="end-midi-qol-hits-display">${grapple_result}`;
                    content = await content.replace(searchString, replaceString);
                    await chatMessage.update({ content: content });
                    await ui.chat.scrollBottom();
                }
            }
        },
        default: "attack"
    }).render(true);
} else {
    game.dice3d?.showForRoll(damageRoll);
    await new MidiQOL.DamageOnlyWorkflow(actorD, tokenD, damageRoll.total, damageType, [target], damageRoll, { flavor: `(${CONFIG.DND5E.damageTypes[damageType]})`, itemCardId: lastArg.itemCardId, useOther: false });
}

macro.command:
const itemName = args[0].itemData.name;
const targetActor = args[0].tokenUuid
  ? (await fromUuid(args[0].tokenUuid)).actor
  : game.actors.get(args[0].actorId);

async function updateEffects(html) {
  const element = html.find("#element").val();
  const effect = targetActor.effects.find((i) => (i.name ?? i.label) === `${itemName} - Extra Damage`);
  const changes = duplicate(effect.changes);
  changes[0].value += `[${element}]`;
  changes[1].value += `[${element}]`;
  await effect.update({ changes });
  const resistanceEffect = targetActor.effects.find((i) => (i.name ?? i.label) === `${itemName} - Resistance`);
  const resistanceChanges = duplicate(resistanceEffect.changes);
  resistanceChanges[0].value = element;
  await resistanceEffect.update({ changes: resistanceChanges });
}

await new Promise(resolve => {
  let isButtonClose = false;
  new Dialog({
    title: "Choose a damage type",
    content: `
      <form class="flexcol">
        <div class="form-group">
          <select id="element">
            <option value="acid">Acid</option>
            <option value="cold">Cold</option>
            <option value="fire">Fire</option>
            <option value="lightning">Lightning</option>
            <option value="thunder">Thunder</option>
          </select>
        </div>
      </form>
    `,
    // select element type
    buttons: {
      yes: {
        icon: '<i class="fas fa-bolt"></i>',
        label: "Select",
        callback: async(html) => {
          isButtonClose = true;
          try {
            await updateEffects(html);
          } finally {
            resolve();
          }
        },
      },
    },
    close: () => {
      // Avoid racing the button callback if closed via button
      if (!isButtonClose) resolve();
    },
  }).render(true);
});

// pass in @spellLevel
const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const target = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;

let buf = (parseInt(args[1]) - 1) * 5;
let curHP = target.system.attributes.hp.value;
let curMax = target.system.attributes.hp.max;

if (args[0] === "on") {
  target.update({ "system.attributes.hp.value": curHP + buf });
} else if (args[0] === "off" && curHP > curMax) {
  target.update({ "system.attributes.hp.value": curMax });
}

const lastArg = args[args.length - 1];

async function updateActor(kind, targetActor) {
  DAE.setFlag(targetActor, 'enhanceAbility', { name: kind });
  // const effectName = lastArg.efData.name ?? lastArg.efData.label;
  const effectName = lastArg.itemData.effects[0].name;
  const effect = DDBImporter.lib.DDBEffectHelper.findEffect(targetActor, effectName);

  // console.warn("update actor", {
  //   kind,
  //   effectName,
  //   effect,
  //   targetActor,
  // })
  let changes = [];
  switch (kind) {
    case "bear": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.save.con",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Constitution checks` });
      const amount = await new CONFIG.Dice.DamageRoll("2d6").evaluate({ async: true });
      console.warn(amount);
      if (
        !Number.isInteger(targetActor.system.attributes.hp.temp)
        || amount.total > targetActor.system.attributes.hp.temp
      ) {
        console.log(`${targetActor.name} gains ${amount.total} temp Hp`, amount);
        ChatMessage.create({ content: `${targetActor.name} gains ${amount.total} temp Hp` });
        await targetActor.update({ "system.attributes.hp.temp": amount.total });
      }
      break;
    }
    case "bull": {
      changes = [{
        key: "flags.dnd5e.powerfulBuild",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      },
      {
        key: "flags.midi-qol.advantage.ability.check.str",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name}'s encumbrance is doubled` });
      ChatMessage.create({ content: `${targetActor.name} has advantage on Strength checks` });
      break;
    }
    case "cat": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.dex",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({
        content: `${targetActor.name} doesn't take damage from falling 20 feet or less if it isn't incapacitated.`,
      });
      ChatMessage.create({ content: `${targetActor.name} has advantage on Dexterity checks` });
      break;
    }
    case "eagle": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.cha",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Charisma checks` });
      break;
    }
    case "fox": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.int",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Intelligence checks` });
      break;
    }
    case "owl": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.wis",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Wisdom checks` });
      break;
    }
  }
  if (changes.length > 0) {
    console.log(`Applying ${kind} changes to ${targetActor.name}`, changes);
    await effect.update({ changes: changes.concat(effect.changes) });
  }

}

/**
 * For each target select the effect (GM selection)
 */
if (lastArg.tag === "OnUse" && lastArg.macroPass === "postActiveEffects") {
  if (lastArg.targets.length < 1) {
    ui.notifications.error("Enhance Ability: No target selected: unable to automate effect.");
    return;
  }
  for (const target of lastArg.targets) {
    await new Dialog({
      title: "Choose Enhance Ability option for " + target.name,
      content: "<p>Choose option</p>",
      buttons: {
        one: {
          label: "Bear's Endurance",
          callback: async () => await updateActor("bear", target.actor),
        },
        two: {
          label: "Bull's Strength",
          callback: async () => await updateActor("bull", target.actor),
        },
        three: {
          label: "Cat's Grace",
          callback: async () => await updateActor("cat", target.actor),
        },
        four: {
          label: "Eagle's Splendor",
          callback: async () => await updateActor("eagle", target.actor),
        },
        five: {
          label: "Fox's Cunning",
          callback: async () => await updateActor("fox", target.actor),
        },
        six: {
          label: "Owl's Wisdom",
          callback: async () => await updateActor("owl", target.actor),
        },
      },
    }).render(true);
  }
}

if (args[0] === "off") {
  // console.warn("off called", { args, scope })
  const tokenOrActor = await fromUuid(lastArg.actorUuid);
  const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
  const flag = await DAE.getFlag(targetActor, 'enhanceAbility');
  if (flag.name === "bear") {
    await targetActor.update({ "system.attributes.hp.temp": "" });
    await DAE.unsetFlag(targetActor, "enhanceAbility");
  }
  ChatMessage.create({ content: "Enhance Ability has expired" });
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;

function valueLimit(val, min, max) {
  return val < min ? min : val > max ? max : val;
}

/**
 * Select for weapon and apply bonus based on spell level
 */
if (args[0] === "on") {
  const weapons = targetActor.items.filter((i) => i.type === "weapon");
  let weaponContent = "";

  // Filter for weapons
  weapons.forEach((weapon) => {
    weaponContent += `<label class="radio-label">
  <input type="radio" name="weapon" value="${weapon.id}">
  <img src="${weapon.img}" style="border:0px; width: 50px; height:50px;">
  ${weapon.name}
</label>`;
  });

  const content = `
    <style>
    .magicWeapon .form-group {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        align-items: flex-start;
      }

      .magicWeapon .radio-label {
        display: flex;
        flex-direction: column;
        align-items: center;
        text-align: center;
        justify-items: center;
        flex: 1 0 25%;
        line-height: normal;
      }

      .magicWeapon .radio-label input {
        display: none;
      }

      .magicWeapon img {
        border: 0px;
        width: 50px;
        height: 50px;
        flex: 0 0 50px;
        cursor: pointer;
      }

      /* CHECKED STYLES */
      .magicWeapon [type=radio]:checked + img {
        outline: 2px solid #f00;
      }
    </style>
    <form class="magicWeapon">
      <div class="form-group" id="weapons">
          ${weaponContent}
      </div>
    </form>
`;

  new Dialog({
    content,
    buttons: {
      ok: {
        label: `Ok`,
        callback: () => {
          const itemId = $("input[type='radio'][name='weapon']:checked").val();
          const weaponItem = targetActor.items.get(itemId);
          let copyItem = duplicate(weaponItem);
          const spellLevel = Math.floor(args[1] / 2);
          const bonus = valueLimit(spellLevel, 1, 3);
          const wpDamage = copyItem.system.damage.parts[0][0];
          const verDamage = copyItem.system.damage.versatile;
          DAE.setFlag(targetActor, "magicWeapon", {
            damage: weaponItem.system.attackBonus,
            weapon: itemId,
            weaponDmg: wpDamage,
            verDmg: verDamage,
            mgc: copyItem.system.properties.mgc,
          });
          if (copyItem.system.attackBonus === "") copyItem.system.attackBonus = "0";
          copyItem.system.attackBonus = `${parseInt(copyItem.system.attackBonus) + bonus}`;
          copyItem.system.damage.parts[0][0] = wpDamage + " + " + bonus;
          copyItem.system.properties.mgc = true;
          if (verDamage !== "" && verDamage !== null) copyItem.system.damage.versatile = verDamage + " + " + bonus;
          targetActor.updateEmbeddedDocuments("Item", [copyItem]);
        },
      },
      cancel: {
        label: `Cancel`,
      },
    },
  }).render(true);
}

// Revert weapon and unset flag.
if (args[0] === "off") {
  const { damage, weapon, weaponDmg, verDmg, mgc } = DAE.getFlag(targetActor, "magicWeapon");
  const weaponItem = targetActor.items.get(weapon);
  let copyItem = duplicate(weaponItem);
  copyItem.system.attackBonus = damage;
  copyItem.system.damage.parts[0][0] = weaponDmg;
  copyItem.system.properties.mgc = mgc;
  if (verDmg !== "" && verDmg !== null) copyItem.system.damage.versatile = verDmg;
  targetActor.updateEmbeddedDocuments("Item", [copyItem]);
  DAE.unsetFlag(targetActor, "magicWeapon");
}

if(!game.modules.get("ActiveAuras")?.active) {
  ui.notifications.error("ActiveAuras is not enabled");
  return;
} else if (!game.modules.get("ddb-importer")?.active) {
  ui.notifications.error("ddb-importer is not enabled");
  return;
}

const lastArg = args[args.length - 1];

console.warn("ARGS", {
  args,
  lastArg,
})

async function attemptRemoval(targetToken, condition, item) {
  if (game.dfreds.effectInterface.hasEffectApplied(condition, targetToken.document.uuid)) {
    new Dialog({
      title: `Use action to attempt to remove ${condition}?`,
      buttons: {
        one: {
          label: "Yes",
          callback: async () => {
            const caster = item.parent;
            const saveDc = caster.system.attributes.spelldc;
            const removalCheck = item.flags.ddbimporter.effect.removalCheck;
            const removalSave = item.flags.ddbimporter.effect.removalSave;
            const ability = removalCheck ? removalCheck : removalSave;
            const type = removalCheck ? "check" : "save";
            const flavor = `${condition} (via ${item.name}) : ${CONFIG.DND5E.abilities[ability].label} ${type} vs DC${saveDc}`;
            const rollResult = removalCheck
              ? (await targetToken.actor.rollAbilityTest(ability, { flavor })).total
              : (await targetToken.actor.rollAbilitySave(ability, { flavor })).total;

            if (rollResult >= saveDc) {
              game.dfreds.effectInterface.removeEffect({ effectName: condition, uuid: targetToken.document.uuid });
            } else {
              if (rollResult < saveDc) ChatMessage.create({ content: `${targetToken.name} fails the ${type} for ${item.name}, still has the ${condition} condition.` });
            }
          },
        },
        two: {
          label: "No",
          callback: () => {},
        },
      },
    }).render(true);
  }
}

async function applyCondition(condition, targetToken, item, itemLevel) {
  if (!game.dfreds.effectInterface.hasEffectApplied(condition, targetToken.document.uuid)) {
    const caster = item.parent;
    const workflowItemData = duplicate(item);
    workflowItemData.system.target = { value: 1, units: "", type: "creature" };
    workflowItemData.system.save.ability = item.flags.ddbimporter.effect.save;
    workflowItemData.system.components.concentration = false;
    workflowItemData.system.level = itemLevel;
    workflowItemData.system.duration = { value: null, units: "inst" };
    workflowItemData.system.target = { value: null, width: null, units: "", type: "creature" };
    workflowItemData.system.uses = { value: null, max: "", per: null, recovery: "", autoDestroy: false };
    workflowItemData.system.consume = { "type": "", "target": null, "amount": null };
    workflowItemData.system.preparation.mode = "atwill";
    setProperty(workflowItemData, "flags.itemacro", {});
    setProperty(workflowItemData, "flags.midi-qol", {});
    setProperty(workflowItemData, "flags.dae", {});
    setProperty(workflowItemData, "effects", []);
    delete workflowItemData._id;
    workflowItemData.name = `${workflowItemData.name}: ${item.name} Condition save`;

    const saveTargets = [...game.user?.targets].map((t )=> t.id);
    game.user.updateTokenTargets([targetToken.id]);
    const saveItem = new CONFIG.Item.documentClass(workflowItemData, { parent: caster });
    const options = { showFullCard: false, createWorkflow: true, configureDialog: true };
    const result = await MidiQOL.completeItemUse(saveItem, {}, options);

    game.user.updateTokenTargets(saveTargets);
    const failedSaves = [...result.failedSaves];
    if (failedSaves.length > 0) {
      await game.dfreds.effectInterface.addEffect({ effectName: condition, uuid: failedSaves[0].document.uuid });
    }

    return result;
  }
}

if (args[0].tag === "OnUse" && args[0].macroPass === "preActiveEffects") {
  const safeName = lastArg.itemData.name.replace(/\s|'|\.|’/g, "_");
  const dataTracker = {
    randomId: randomID(),
    targetUuids: lastArg.targetUuids,
    startRound: game.combat.round,
    startTurn: game.combat.turn,
    spellLevel: lastArg.spellLevel,
  };

  const item = await fromUuid(lastArg.itemUuid);
  // await item.update(dataTracker);
  await DAE.unsetFlag(item.actor, `${safeName}Tracker`);
  await DAE.setFlag(item.actor, `${safeName}Tracker`, dataTracker);

  const sequencerFile = lastArg.item.flags.ddbimporter?.effect?.sequencerFile;
  if (sequencerFile) {
    const scale = lastArg.item.flags.ddbimporter.effect.sequencerScale ?? 1;
    await DDBImporter?.EffectHelper.attachSequencerFileToTemplate(lastArg.templateUuid, sequencerFile, lastArg.itemUuid, scale);
  }

  if (lastArg.item.flags.ddbimporter?.effect?.applyImmediate) {
    await DDBImporter?.EffectHelper.wait(500);
    const condition = lastArg.item.flags.ddbimporter.effect.condition;
    for (const token of lastArg.failedSaves) {
      if (!game.dfreds.effectInterface.hasEffectApplied(condition, token.actor.uuid)) {
        console.debug(`Applying ${condition} to ${token.name}`);
        await game.dfreds.effectInterface.addEffect({ effectName: condition, uuid: token.actor.uuid });
      }
    };
  }

  return await game.modules.get("ActiveAuras").api.AAHelpers.applyTemplate(args);

} else if (args[0].tag === "OnUse" && args[0].macroPass === "postActiveEffects") {
  if (lastArg.item.flags.ddbimporter?.effect?.applyImmediate) {
    const condition = lastArg.item.flags.ddbimporter.effect.condition;
    for (const token of lastArg.failedSaves) {
      if (!game.dfreds.effectInterface.hasEffectApplied(condition, token.actor.uuid)) {
        console.debug(`Applying ${condition} to ${token.name}`);
        await game.dfreds.effectInterface.addEffect({ effectName: condition, uuid: token.actor.uuid });
      }
    };
  }
} else if (args[0] == "on" || args[0] == "each") {
  const safeName = (lastArg.efData.name ?? lastArg.efData.label).replace(/\s|'|\.|’/g, "_");
  const item = await fromUuid(lastArg.efData.origin);
  // sometimes the round info has not updated, so we pause a bit
  if (args[0] == "each") await DDBImporter?.EffectHelper.wait(500);
  const targetItemTracker = DAE.getFlag(item.parent, `${safeName}Tracker`);
  const originalTarget = targetItemTracker.targetUuids.includes(lastArg.tokenUuid);
  const target = canvas.tokens.get(lastArg.tokenId);
  const targetTokenTrackerFlag = DAE.getFlag(target.actor, `${safeName}Tracker`);
  const targetedThisCombat = targetTokenTrackerFlag && targetItemTracker.randomId === targetTokenTrackerFlag.randomId;
  const targetTokenTracker = targetedThisCombat
    ? targetTokenTrackerFlag
    : {
      randomId: targetItemTracker.randomId,
      round: game.combat.round,
      turn: game.combat.turn,
      hasLeft: false,
      condition: item.flags.ddbimporter.effect.condition,
    };

  const castTurn = targetItemTracker.startRound === game.combat.round && targetItemTracker.startTurn === game.combat.turn;
  const isLaterTurn = game.combat.round > targetTokenTracker.round || game.combat.turn > targetTokenTracker.turn;
  const everyEntry = hasProperty(item, "flags.ddbimporter.effect.everyEntry")
    ? item.flags.ddbimporter.effect.everyEntry
    : false;

  // if:
  // not cast turn, and not part of the original target
  // AND one of the following
  // not original template and have not yet had this effect applied this combat OR
  // has been targeted this combat, left and re-entered effect, and is a later turn

  if (castTurn && originalTarget) {
    console.debug(`Token ${target.name} is part of the original target for ${item.name}`);
  } else if (everyEntry || !targetedThisCombat || (targetedThisCombat && isLaterTurn)) {
    console.debug(`Token ${target.name} is targeted for immediate save vs condition with ${item.name}, using the following factors`, { originalTarget, castTurn, targetedThisCombat, targetTokenTracker, isLaterTurn });
    targetTokenTracker.hasLeft = false;
    await applyCondition(targetTokenTracker.condition, target, item, targetItemTracker.spellLevel);
  }
  await DAE.setFlag(target.actor, `${safeName}Tracker`, targetTokenTracker);
  const allowVsRemoveCondition = item.flags.ddbimporter.effect.allowVsRemoveCondition;
  const effectApplied = game.dfreds.effectInterface.hasEffectApplied(targetTokenTracker.condition, target.document.uuid);
  const currentTokenCombatTurn = game.combat.current.tokenId === lastArg.tokenId;
  if (currentTokenCombatTurn && allowVsRemoveCondition && effectApplied) {
    console.log(`Removing ${targetTokenTracker.condition}`);
    await attemptRemoval(target, targetTokenTracker.condition, item);
  }
} else if (args[0] == "off") {
  const safeName = (lastArg.efData.name ?? lastArg.efData.label).replace(/\s|'|\.|’/g, "_");
  const targetToken = await fromUuid(lastArg.tokenUuid);
  console.warn("off args", {
    targetToken,
    tokenUuid: lastArg.tokenUuid,
  })
  const targetTokenTracker = await DAE.getFlag(targetToken.actor, `${safeName}Tracker`);
  console.warn("targetTokenTracker", targetTokenTracker);
  const removeOnOff = hasProperty(lastArg, "efData.flags.ddbimporter.effect.removeOnOff")
    ? lastArg.efData.flags.ddbimporter.effect.removeOnOff
    : true;

  if (targetTokenTracker?.condition && removeOnOff && game.dfreds.effectInterface.hasEffectApplied(targetTokenTracker.condition, lastArg.tokenUuid)) {
    console.debug(`Removing ${targetTokenTracker.condition} from ${targetToken.name}`);
    game.dfreds.effectInterface.removeEffect({ effectName: targetTokenTracker.condition, uuid: lastArg.tokenUuid });
  }

  if (targetTokenTracker) {
    targetTokenTracker.hasLeft = true;
    targetTokenTracker.turn = game.combat.turn;
    targetTokenTracker.round = game.combat.round;
    await DAE.setFlag(targetToken.actor, `${safeName}Tracker`, targetTokenTracker);
  }
}
