/**
 * Execute contagion effects, update flag counts or remove effect
 *
 * @param {Actor5e} combatant Current combatant to test against
 * @param {Number} save Target DC for save
 */
async function contagionSave() {
  const flag = DAE.getFlag(targetActor, "ContagionSpell");
  const flavor = `${CONFIG.DND5E.abilities["con"].label} DC${flag.saveDC} ${DAEItem?.name || ""}`;
  const saveRoll = await targetActor.rollAbilitySave("con", { flavor });

  if (saveRoll.total < flag.saveDC) {
    if (flag.count === 2) {
      ChatMessage.create({ content: `Contagion on ${targetActor.name} is complete` });
      applyContagion();
    } else {
      const contagionCount = flag.count + 1;
      DAE.setFlag(targetActor, "ContagionSpell", { count: contagionCount });
      console.log(`Contagion increased to ${contagionCount}`);
    }
  } else if (saveRoll.total >= flag.saveDC) {
    targetActor.deleteEmbeddedDocuments("ActiveEffect", [lastArg.effectId]);
  }
}

if (args[0] === "on") {
  const saveData = DAEItem.system.save;
  if (saveData.scaling === "spell") {
    const rollData = actor.getRollData();
    saveData.dc = rollData.attributes.spelldc;
  }
  // Save the hook data for later access.
  DAE.setFlag(targetActor, "ContagionSpell", { count: 0, saveDC: saveData.dc });
}

if (args[0] === "off") {
  // When off, clean up hooks and flags.
  DAE.unsetFlag(targetActor, "ContagionSpell");
}

if (args[0] === "each") {
  let contagion = lastArg.efData;
  if ((contagion.name ?? contagion.label) === "Contagion") contagionSave();
}

async function wait(ms) { return new Promise(resolve => { setTimeout(resolve, ms); }); }
const lastArg = args[args.length - 1];
const version = Math.floor(game.version);
const tokenD = canvas.tokens.get(lastArg.tokenId);
const actorD = tokenD.actor;
const actorData = actorD.getRollData();
let guided = actorD.itemTypes.feat.find(item => ["guided strike"].some(x => (item.name).toLowerCase().includes(x)));
let itemD = lastArg.item;
if (!guided) return ui.notifications.error(`Looking for an item named Guided Strike, cannot find it.`);
let target = canvas.tokens.get(lastArg.targets[0].id);
if (!["ak"].some(i => (version > 9 ? itemD.actionType : itemD.data.actionType).toLowerCase().includes(i))) return {};
let resourceName = "Channel Divinity";
let resourceList = [{ name: "primary" }, { name: "secondary" }, { name: "tertiary" }];
let resourceValues = Object.values(actorData.resources);
let resourceTable = mergeObject(resourceList, resourceValues);
let findResourceSlot = resourceTable.find(i => i.label.toLowerCase() === resourceName.toLowerCase());
if (!findResourceSlot) return ui.notifications.error(`${resourceName} Resources is missing on ${tokenD.name}, Add it.`);
let resourceSlot = findResourceSlot.name;
let curtRes = actorData.resources[resourceSlot].value;
let curtMax = actorData.resources[resourceSlot].value;
if (curtRes < 1) return console.warn(guided.name, curtRes, `out of resources`);
if (lastArg.macroPass === "preCheckHits") {
    let workflow = await MidiQOL.Workflow.getWorkflow(lastArg.uuid);
    let guidingStrike = await new Promise((resolve) => {
        let the_content = `<p>Attack Roll Total: <span style="font-weight:800;color: ${lastArg.attackD20 === 1 ? "red" : lastArg.attackD20 === 20 ? "green" : "black"}">${lastArg.attackTotal}</span> <small>[ ${lastArg.attackRoll.result} ]</small></p><p>Do you wish to Channel Divinity to guide your attack? [${curtRes}/${curtMax}]</p>`;
        new Dialog({
            title: guided.name,
            content: the_content,
            buttons: {
                use: {
                    label: `Attack`, icon: `<img src="${version > 9 ? guided.img : guided.data.img}" width="30px" height="30px">`, callback: async () => resolve(true)
                },
                skip: {
                    label: "Skip", callback: () => resolve(false)
                }
            },
            default: "Skip"
        }).render(true);
    });
    if (!guidingStrike) return {};
    console.warn(guided.name, `Original Attack Data =>`, `Attack Total:`, lastArg.attackTotal);
    let newAttackRoll = await new Roll(`${lastArg.attackRoll.result} + 10`).evaluate({ async: true });
    console.warn(guided.name, `Adjusted Attack Data =>`, `Attack Total:`, newAttackRoll.total);
    workflow.isFumble = false;
    workflow.isCritical = false;
    if (lastArg.attackD20 === 1) {
        workflow.isFumble = true
    } else if (lastArg.attackD20 === 20) {
        workflow.isCritical = true;
    } else {
        workflow.attackRoll = newAttackRoll;
        workflow.attackTotal = newAttackRoll.total;
    }
    switch (MidiQOL.configSettings().rollAlternate) {
        case "formula":
        case "formulaadv": workflow.attackRollHTML = await newAttackRoll.render({ template: "modules/midi-qol/templates/rollAlternate.html" }); break;
        case "adv":
        case "off":
        default: workflow.attackRollHTML = await newAttackRoll.render(); // "off"
    }
    let updates = {};
    let resources = `data.resources.${resourceSlot}.value`;
    updates[resources] = curtRes - 1;
    await actorD.update(updates);
    await wait(300);
    let the_message = `<em>${guided.name}.<br>${tokenD.name} strikes with supernatural accuracy!</em>`;
    let chatMessage = game.messages.get(lastArg.itemCardId);
    let content = await duplicate(version > 9 ? chatMessage.content : chatMessage.data.content);
    let searchString = /<div class="midi-qol-saves-display">[\s\S]*<div class="end-midi-qol-saves-display">/g;
    let replaceString = `<div class="midi-qol-saves-display"><div class="end-midi-qol-saves-display"><hr>${the_message}`;
    content = content.replace(searchString, replaceString);
    await chatMessage.update({ content: content });
    await ui.chat.scrollBottom();
}

// pass in @spellLevel
const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const target = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;

let buf = (parseInt(args[1]) - 1) * 5;
let curHP = target.system.attributes.hp.value;
let curMax = target.system.attributes.hp.max;

if (args[0] === "on") {
  target.update({ "system.attributes.hp.value": curHP + buf });
} else if (args[0] === "off" && curHP > curMax) {
  target.update({ "system.attributes.hp.value": curMax });
}

const lastArg = args[args.length - 1];
const target = await fromUuid(lastArg.tokenUuid);

if (args[0] === "on") {
  await target.update({ hidden: true }); // hide targeted token
  ChatMessage.create({ content: target.name + " was banished" });

}
if (args[0] === "off") {
  await target.update({ hidden: false }); // unhide token
  ChatMessage.create({ content: target.name + " returned" });
}

if (!game.modules.get("dfreds-convenient-effects")?.active) {
  ui.notifications.error("Please enable the CE module");
  return;
}

const lastArg = args[args.length - 1];

if (lastArg.tag === "OnUse") {
  if (lastArg.targets.length < 1) {
    ui.notifications.error("Blindness/Deafness: No target selected: unable to automate effect.");
    return;
  }
  new Dialog({
    title: "Choose an Effect",
    buttons: {
      blind: {
        label: "Blindness",
        callback: () => {
          lastArg.targets.forEach((targetToken) => {
            const targetActor = targetToken.actor;
            DAE.setFlag(targetActor, "DAEBlind", "blind");
            game.dfreds.effectInterface.addEffect({ effectName: "Blinded", uuid: targetActor.uuid });
            const changes = [
              {
                key: "ATL.sight.range",
                mode: CONST.ACTIVE_EFFECT_MODES.OVERRIDE,
                priority: 99,
                value: "0",
              },
            ];
            const effectName = lastArg.itemData.effects[0].name;
            const effect = DDBImporter.lib.DDBEffectHelper.findEffect(targetActor, effectName);
            // console.warn("effect", {effect, effectName});
            effect.update({ changes: changes.concat(effect.changes) });
          });
        },
      },
      deaf: {
        label: "Deafness",
        callback: () => {
          lastArg.targets.forEach((targetToken) => {
            const targetActor = targetToken.actor;
            DAE.setFlag(targetActor, "DAEBlind", "deaf");
            game.dfreds.effectInterface.addEffect({ effectName: "Deafened", uuid: targetActor.uuid });
          });
        },
      },
    },
  }).render(true);
}

if (args[0] === "off") {
  const tokenOrActor = await fromUuid(lastArg.actorUuid);
  const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
  let flag = DAE.getFlag(targetActor, "DAEBlind");
  if (flag === "blind") {
    if (DDBImporter.lib.DDBEffectHelper.effectConditionAppliedAndActive("Blinded", targetActor))
      game.dfreds.effectInterface.removeEffect({ effectName: "Blinded", uuid: targetActor.uuid });
  } else if (flag === "deaf") {
    if (DDBImporter.lib.DDBEffectHelper.effectConditionAppliedAndActive("Deafened", targetActor))
      game.dfreds.effectInterface.removeEffect({ effectName: "Deafened", uuid: targetActor.uuid });
  }
  DAE.unsetFlag(targetActor, "DAEBlind");
}

const lastArg = args[args.length - 1];

async function updateActor(kind, targetActor) {
  DAE.setFlag(targetActor, 'enhanceAbility', { name: kind });
  // const effectName = lastArg.efData.name ?? lastArg.efData.label;
  const effectName = lastArg.itemData.effects[0].name;
  const effect = DDBImporter.lib.DDBEffectHelper.findEffect(targetActor, effectName);

  // console.warn("update actor", {
  //   kind,
  //   effectName,
  //   effect,
  //   targetActor,
  // })
  let changes = [];
  switch (kind) {
    case "bear": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.save.con",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Constitution checks` });
      const amount = await new CONFIG.Dice.DamageRoll("2d6").evaluate({ async: true });
      console.warn(amount);
      if (
        !Number.isInteger(targetActor.system.attributes.hp.temp)
        || amount.total > targetActor.system.attributes.hp.temp
      ) {
        console.log(`${targetActor.name} gains ${amount.total} temp Hp`, amount);
        ChatMessage.create({ content: `${targetActor.name} gains ${amount.total} temp Hp` });
        await targetActor.update({ "system.attributes.hp.temp": amount.total });
      }
      break;
    }
    case "bull": {
      changes = [{
        key: "flags.dnd5e.powerfulBuild",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      },
      {
        key: "flags.midi-qol.advantage.ability.check.str",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name}'s encumbrance is doubled` });
      ChatMessage.create({ content: `${targetActor.name} has advantage on Strength checks` });
      break;
    }
    case "cat": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.dex",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({
        content: `${targetActor.name} doesn't take damage from falling 20 feet or less if it isn't incapacitated.`,
      });
      ChatMessage.create({ content: `${targetActor.name} has advantage on Dexterity checks` });
      break;
    }
    case "eagle": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.cha",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Charisma checks` });
      break;
    }
    case "fox": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.int",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Intelligence checks` });
      break;
    }
    case "owl": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.wis",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Wisdom checks` });
      break;
    }
  }
  if (changes.length > 0) {
    console.log(`Applying ${kind} changes to ${targetActor.name}`, changes);
    await effect.update({ changes: changes.concat(effect.changes) });
  }

}

/**
 * For each target select the effect (GM selection)
 */
if (lastArg.tag === "OnUse" && lastArg.macroPass === "postActiveEffects") {
  if (lastArg.targets.length < 1) {
    ui.notifications.error("Enhance Ability: No target selected: unable to automate effect.");
    return;
  }
  for (const target of lastArg.targets) {
    await new Dialog({
      title: "Choose Enhance Ability option for " + target.name,
      content: "<p>Choose option</p>",
      buttons: {
        one: {
          label: "Bear's Endurance",
          callback: async () => await updateActor("bear", target.actor),
        },
        two: {
          label: "Bull's Strength",
          callback: async () => await updateActor("bull", target.actor),
        },
        three: {
          label: "Cat's Grace",
          callback: async () => await updateActor("cat", target.actor),
        },
        four: {
          label: "Eagle's Splendor",
          callback: async () => await updateActor("eagle", target.actor),
        },
        five: {
          label: "Fox's Cunning",
          callback: async () => await updateActor("fox", target.actor),
        },
        six: {
          label: "Owl's Wisdom",
          callback: async () => await updateActor("owl", target.actor),
        },
      },
    }).render(true);
  }
}

if (args[0] === "off") {
  // console.warn("off called", { args, scope })
  const tokenOrActor = await fromUuid(lastArg.actorUuid);
  const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
  const flag = await DAE.getFlag(targetActor, 'enhanceAbility');
  if (flag.name === "bear") {
    await targetActor.update({ "system.attributes.hp.temp": "" });
    await DAE.unsetFlag(targetActor, "enhanceAbility");
  }
  ChatMessage.create({ content: "Enhance Ability has expired" });
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;

function valueLimit(val, min, max) {
  return val < min ? min : val > max ? max : val;
}

/**
 * Select for weapon and apply bonus based on spell level
 */
if (args[0] === "on") {
  const weapons = targetActor.items.filter((i) => i.type === "weapon");
  let weaponContent = "";

  // Filter for weapons
  weapons.forEach((weapon) => {
    weaponContent += `<label class="radio-label">
  <input type="radio" name="weapon" value="${weapon.id}">
  <img src="${weapon.img}" style="border:0px; width: 50px; height:50px;">
  ${weapon.name}
</label>`;
  });

  const content = `
    <style>
    .magicWeapon .form-group {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        align-items: flex-start;
      }

      .magicWeapon .radio-label {
        display: flex;
        flex-direction: column;
        align-items: center;
        text-align: center;
        justify-items: center;
        flex: 1 0 25%;
        line-height: normal;
      }

      .magicWeapon .radio-label input {
        display: none;
      }

      .magicWeapon img {
        border: 0px;
        width: 50px;
        height: 50px;
        flex: 0 0 50px;
        cursor: pointer;
      }

      /* CHECKED STYLES */
      .magicWeapon [type=radio]:checked + img {
        outline: 2px solid #f00;
      }
    </style>
    <form class="magicWeapon">
      <div class="form-group" id="weapons">
          ${weaponContent}
      </div>
    </form>
`;

  new Dialog({
    content,
    buttons: {
      ok: {
        label: `Ok`,
        callback: () => {
          const itemId = $("input[type='radio'][name='weapon']:checked").val();
          const weaponItem = targetActor.items.get(itemId);
          let copyItem = duplicate(weaponItem);
          const spellLevel = Math.floor(args[1] / 2);
          const bonus = valueLimit(spellLevel, 1, 3);
          const wpDamage = copyItem.system.damage.parts[0][0];
          const verDamage = copyItem.system.damage.versatile;
          DAE.setFlag(targetActor, "magicWeapon", {
            damage: weaponItem.system.attackBonus,
            weapon: itemId,
            weaponDmg: wpDamage,
            verDmg: verDamage,
            mgc: copyItem.system.properties.mgc,
          });
          if (copyItem.system.attackBonus === "") copyItem.system.attackBonus = "0";
          copyItem.system.attackBonus = `${parseInt(copyItem.system.attackBonus) + bonus}`;
          copyItem.system.damage.parts[0][0] = wpDamage + " + " + bonus;
          copyItem.system.properties.mgc = true;
          if (verDamage !== "" && verDamage !== null) copyItem.system.damage.versatile = verDamage + " + " + bonus;
          targetActor.updateEmbeddedDocuments("Item", [copyItem]);
        },
      },
      cancel: {
        label: `Cancel`,
      },
    },
  }).render(true);
}

// Revert weapon and unset flag.
if (args[0] === "off") {
  const { damage, weapon, weaponDmg, verDmg, mgc } = DAE.getFlag(targetActor, "magicWeapon");
  const weaponItem = targetActor.items.get(weapon);
  let copyItem = duplicate(weaponItem);
  copyItem.system.attackBonus = damage;
  copyItem.system.damage.parts[0][0] = weaponDmg;
  copyItem.system.properties.mgc = mgc;
  if (verDmg !== "" && verDmg !== null) copyItem.system.damage.versatile = verDmg;
  targetActor.updateEmbeddedDocuments("Item", [copyItem]);
  DAE.unsetFlag(targetActor, "magicWeapon");
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const content = `
<style>
  .protEnergy .form-group {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    align-items: flex-start;
  }
  .protEnergy .radio-label {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    justify-items: center;
    flex: 1 0 20%;
    line-height: normal;
  }
  .protEnergy .radio-label input {
    display: none;
  }
  .protEnergy img {
    border: 0px;
    width: 50px;
    height: 50px;
    flex: 0 0 50px;
    cursor: pointer;
  }
  /* CHECKED STYLES */
  .protEnergy [type="radio"]:checked + img {
    outline: 2px solid #f00;
  }
</style>
<form class="protEnergy">
  <div class="form-group" id="types">
    <label class="radio-label">
      <input type="radio" name="type" value="acid" />
      <img
        src="icons/magic/acid/dissolve-bone-white.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Acid
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="cold" />
      <img
        src="icons/magic/water/barrier-ice-crystal-wall-jagged-blue.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Cold
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="fire" />
      <img
        src="icons/magic/fire/barrier-wall-flame-ring-yellow.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Fire
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="lightning" />
      <img
        src="icons/magic/lightning/bolt-strike-blue.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Lighting
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="thunder" />
      <img
        src="icons/magic/sonic/explosion-shock-wave-teal.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Thunder
    </label>
  </div>
</form>
`;
if (args[0] === "on") {
  new Dialog({
    title: 'Choose a damage type',
    content: content,
    buttons: {
      yes: {
        icon: '<i class="fas fa-check"></i>',
        label: 'Protect!',
        callback: async () => {
          const element = $("input[type='radio'][name='type']:checked").val();
          const effect = targetActor.effects.find((e) => (e.name ?? e.label) === (lastArg.efData.name ?? lastArg.efData.label));
          const changes = [
            {
              key: "data.traits.dr.value",
              mode: CONST.ACTIVE_EFFECT_MODES.ADD,
              priority: 30,
              value: element,
            },
          ];
          await effect.update({ changes: changes.concat(effect.changes) });
          await DAE.setFlag(targetActor, "protectionFromEnergySpell", element);
          ChatMessage.create({ content: `${targetActor.name} gains resistance to ${element}` });
        }
      },
    },
  }).render(true, { width: 400 });
}
if (args[0] === "off") {
  const element = DAE.getFlag(targetActor, 'protectionFromEnergySpell');
  await DAE.unsetFlag(targetActor, 'protectionFromEnergySpell');
  ChatMessage.create({ content: `${targetActor.name} loses resistance to ${element}` });
}

if(!game.modules.get("ActiveAuras")?.active) {
  ui.notifications.error("ActiveAuras is not enabled");
  return;
}

if (args[0].macroPass === "preActiveEffects" || args[0].tag === "OnUse") {
  return await game.modules.get("ActiveAuras").api.AAHelpers.applyTemplate(args);
}
