macro.command:
// pass in @spellLevel
const lastArg = args[args.length - 1];
await DDBImporter?.EffectHelper.wait(500);

const tokenOrActor = await fromUuid(lastArg.actorUuid);
const target = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;

let buf = (parseInt(args[1]) - 1) * 5;
let curHP = target.system.attributes.hp.value;
let curMax = target.system.attributes.hp.max;

if (args[0] === "on") {
  target.update({ "system.attributes.hp.value": curHP + buf });
} else if (args[0] === "off" && curHP > curMax) {
  target.update({ "system.attributes.hp.value": curMax });
}

const lastArg = args[args.length - 1];

if (lastArg.tag === "OnUse") {
  if (lastArg.targets.length < 1) {
    ui.notifications.error("Blindness/Deafness: No target selected: unable to automate effect.");
    return;
  }
  new Dialog({
    title: "Choose an Effect",
    buttons: {
      blind: {
        label: "Blindness",
        callback: () => {
          lastArg.targets.forEach((targetToken) => {
            const targetActor = targetToken.actor;
            DAE.setFlag(targetActor, "DAEBlind", "blind");
            globalThis.DDBImporter.EffectHelper.adjustCondition({ add: true, conditionName: "Blinded", actor: targetActor });
            const changes = [
              {
                key: "ATL.sight.range",
                mode: CONST.ACTIVE_EFFECT_MODES.OVERRIDE,
                priority: 99,
                value: "0",
              },
            ];
            const effectName = lastArg.itemData.effects[0].name;
            const effect = DDBImporter.EffectHelper.findEffect(targetActor, effectName);
            // console.warn("effect", {effect, effectName});
            effect.update({ changes: changes.concat(effect.changes) });
          });
        },
      },
      deaf: {
        label: "Deafness",
        callback: () => {
          lastArg.targets.forEach((targetToken) => {
            const targetActor = targetToken.actor;
            DAE.setFlag(targetActor, "DAEBlind", "deaf");
            globalThis.DDBImporter.EffectHelper.adjustCondition({ add: true, conditionName: "Deafened", actor: targetActor });
          });
        },
      },
    },
  }).render(true);
}

if (args[0] === "off") {
  const tokenOrActor = await fromUuid(lastArg.actorUuid);
  const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
  let flag = DAE.getFlag(targetActor, "DAEBlind");
  if (flag === "blind") {
    if (DDBImporter.EffectHelper.isConditionEffectAppliedAndActive("Blinded", targetActor))
      globalThis.DDBImporter.EffectHelper.adjustCondition({ remove: true, conditionName: "Blinded", actor: targetActor });
  } else if (flag === "deaf") {
    if (DDBImporter.EffectHelper.isConditionEffectAppliedAndActive("Deafened", targetActor))
      globalThis.DDBImporter.EffectHelper.adjustCondition({ remove: true, conditionName: "Deafened", actor: targetActor });
  }
  DAE.unsetFlag(targetActor, "DAEBlind");
}

const lastArg = args[args.length - 1];

async function updateActor(kind, targetActor) {
  DAE.setFlag(targetActor, 'enhanceAbility', { name: kind });
  // const effectName = lastArg.efData.name ?? lastArg.efData.label;
  const effectName = lastArg.itemData.effects[0].name;
  const effect = DDBImporter.EffectHelper.findEffect(targetActor, effectName);

  // console.warn("update actor", {
  //   kind,
  //   effectName,
  //   effect,
  //   targetActor,
  // })
  let changes = [];
  switch (kind) {
    case "bear": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.save.con",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Constitution checks` });
      const amount = await new CONFIG.Dice.DamageRoll("2d6").evaluate({ async: true });
      console.warn(amount);
      if (
        !Number.isInteger(targetActor.system.attributes.hp.temp)
        || amount.total > targetActor.system.attributes.hp.temp
      ) {
        console.log(`${targetActor.name} gains ${amount.total} temp Hp`, amount);
        ChatMessage.create({ content: `${targetActor.name} gains ${amount.total} temp Hp` });
        await targetActor.update({ "system.attributes.hp.temp": amount.total });
      }
      break;
    }
    case "bull": {
      changes = [{
        key: "flags.dnd5e.powerfulBuild",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      },
      {
        key: "flags.midi-qol.advantage.ability.check.str",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name}'s encumbrance is doubled` });
      ChatMessage.create({ content: `${targetActor.name} has advantage on Strength checks` });
      break;
    }
    case "cat": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.dex",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({
        content: `${targetActor.name} doesn't take damage from falling 20 feet or less if it isn't incapacitated.`,
      });
      ChatMessage.create({ content: `${targetActor.name} has advantage on Dexterity checks` });
      break;
    }
    case "eagle": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.cha",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Charisma checks` });
      break;
    }
    case "fox": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.int",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Intelligence checks` });
      break;
    }
    case "owl": {
      changes = [{
        key: "flags.midi-qol.advantage.ability.check.wis",
        mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
        priority: 20,
        value: "1",
      }];
      ChatMessage.create({ content: `${targetActor.name} has advantage on Wisdom checks` });
      break;
    }
  }
  if (changes.length > 0) {
    console.log(`Applying ${kind} changes to ${targetActor.name}`, changes);
    await effect.update({ changes: changes.concat(effect.changes) });
  }

}

/**
 * For each target select the effect (GM selection)
 */
if (lastArg.tag === "OnUse" && lastArg.macroPass === "postActiveEffects") {
  if (lastArg.targets.length < 1) {
    ui.notifications.error("Enhance Ability: No target selected: unable to automate effect.");
    return;
  }
  for (const target of lastArg.targets) {
    await new Dialog({
      title: "Choose Enhance Ability option for " + target.name,
      content: "<p>Choose option</p>",
      buttons: {
        one: {
          label: "Bear's Endurance",
          callback: async () => await updateActor("bear", target.actor),
        },
        two: {
          label: "Bull's Strength",
          callback: async () => await updateActor("bull", target.actor),
        },
        three: {
          label: "Cat's Grace",
          callback: async () => await updateActor("cat", target.actor),
        },
        four: {
          label: "Eagle's Splendor",
          callback: async () => await updateActor("eagle", target.actor),
        },
        five: {
          label: "Fox's Cunning",
          callback: async () => await updateActor("fox", target.actor),
        },
        six: {
          label: "Owl's Wisdom",
          callback: async () => await updateActor("owl", target.actor),
        },
      },
    }).render(true);
  }
}

if (args[0] === "off") {
  // console.warn("off called", { args, scope })
  const tokenOrActor = await fromUuid(lastArg.actorUuid);
  const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
  const flag = await DAE.getFlag(targetActor, 'enhanceAbility');
  if (flag.name === "bear") {
    await targetActor.update({ "system.attributes.hp.temp": "" });
    await DAE.unsetFlag(targetActor, "enhanceAbility");
  }
  ChatMessage.create({ content: "Enhance Ability has expired" });
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const content = `
<style>
  .protEnergy .form-group {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    align-items: flex-start;
  }
  .protEnergy .radio-label {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    justify-items: center;
    flex: 1 0 20%;
    line-height: normal;
  }
  .protEnergy .radio-label input {
    display: none;
  }
  .protEnergy img {
    border: 0px;
    width: 50px;
    height: 50px;
    flex: 0 0 50px;
    cursor: pointer;
  }
  /* CHECKED STYLES */
  .protEnergy [type="radio"]:checked + img {
    outline: 2px solid #f00;
  }
</style>
<form class="protEnergy">
  <div class="form-group" id="types">
    <label class="radio-label">
      <input type="radio" name="type" value="acid" />
      <img
        src="icons/magic/acid/dissolve-bone-white.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Acid
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="cold" />
      <img
        src="icons/magic/water/barrier-ice-crystal-wall-jagged-blue.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Cold
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="fire" />
      <img
        src="icons/magic/fire/barrier-wall-flame-ring-yellow.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Fire
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="lightning" />
      <img
        src="icons/magic/lightning/bolt-strike-blue.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Lighting
    </label>
    <label class="radio-label">
      <input type="radio" name="type" value="thunder" />
      <img
        src="icons/magic/sonic/explosion-shock-wave-teal.webp"
        style="border: 0px; width: 50px; height: 50px"
      />
      Thunder
    </label>
  </div>
</form>
`;
if (args[0] === "on") {
  new Dialog({
    title: 'Choose a damage type',
    content: content,
    buttons: {
      yes: {
        icon: '<i class="fas fa-check"></i>',
        label: 'Protect!',
        callback: async () => {
          const element = $("input[type='radio'][name='type']:checked").val();
          const effect = targetActor.effects.find((e) => (e.name ?? e.label) === (lastArg.efData.name ?? lastArg.efData.label));
          const changes = [
            {
              key: "data.traits.dr.value",
              mode: CONST.ACTIVE_EFFECT_MODES.ADD,
              priority: 30,
              value: element,
            },
          ];
          await effect.update({ changes: changes.concat(effect.changes) });
          await DAE.setFlag(targetActor, "protectionFromEnergySpell", element);
          ChatMessage.create({ content: `${targetActor.name} gains resistance to ${element}` });
        }
      },
    },
  }).render(true, { width: 400 });
}
if (args[0] === "off") {
  const element = DAE.getFlag(targetActor, 'protectionFromEnergySpell');
  await DAE.unsetFlag(targetActor, 'protectionFromEnergySpell');
  ChatMessage.create({ content: `${targetActor.name} loses resistance to ${element}` });
}

if(!game.modules.get("ActiveAuras")?.active) {
  ui.notifications.error("ActiveAuras is not enabled");
  return;
}

if (args[0].macroPass === "preActiveEffects" || args[0].tag === "OnUse") {
  return await game.modules.get("ActiveAuras").api.AAHelpers.applyTemplate(args);
}

// based on @ccjmk and @crymic macro for sleep. Gets targets and ignores those who are immune to sleep.
// uses convinient effects
// Midi-qol "On Use"

const sleepHp = await args[0].damageTotal;
const condition = "Unconscious";
console.log(`Sleep Spell => Available HP Pool [${sleepHp}] points`);
const targets = await args[0].targets
  .filter((i) => i.actor.system.attributes.hp.value != 0 && !i.actor.effects.find((x) => (x.name ?? x.label) === condition))
  .sort((a, b) => (canvas.tokens.get(a.id).actor.system.attributes.hp.value < canvas.tokens.get(b.id).actor.system.attributes.hp.value ? -1 : 1));
let remainingSleepHp = sleepHp;
let sleepTarget = [];

for (let target of targets) {
  const findTarget = await canvas.tokens.get(target.id);
  const targetRaceOrType = DDBImporter?.EffectHelper.getRaceOrType(findTarget.actor);
  const immuneType = ["undead", "construct", "elf", "half-elf"].some((race) => targetRaceOrType.includes(race));
  const immuneCI = findTarget.actor.system.traits.ci.custom.includes("Sleep");
  const sleeping = findTarget.actor.effects.find((i) => (i.name ?? i.label) === condition);
  const targetHpValue = findTarget.actor.system.attributes.hp.value;
  const targetImg = target?.texture?.src;
  if ((immuneType) || (immuneCI) || (sleeping)) {
    console.log(`Sleep Results => Target: ${findTarget.name} | HP: ${targetHpValue} | Status: Resists`);
    sleepTarget.push(`<div class="midi-qol-flex-container"><div>Resists</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${targetImg}" width="30" height="30" style="border:0px"></div></div>`);
  } else if (remainingSleepHp >= targetHpValue) {
    remainingSleepHp -= targetHpValue;
    console.log(`Sleep Results => Target: ${findTarget.name} |  HP: ${targetHpValue} | HP Pool: ${remainingSleepHp} | Status: Slept`);
    sleepTarget.push(`<div class="midi-qol-flex-container"><div>Slept</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${targetImg}" width="30" height="30" style="border:0px"></div></div>`);
    const gameRound = game.combat ? game.combat.round : 0;
    const effectData = {
      label: "Sleep Spell",
      name: "Sleep Spell",
      img: "icons/svg/sleep.svg",
      origin: args[0].uuid,
      disabled: false,
      duration: { rounds: 10, seconds: 60, startRound: gameRound, startTime: game.time.worldTime },
      flags: { dae: { specialDuration: ["isDamaged"] } },
      changes: [
        DDBImporter.EffectHelper.generateStatusEffectChange(game.i18n.localize("Unconscious"), 20),
      ]
    };

    await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: findTarget.actor.uuid, effects: [effectData] });
  } else {
    console.log(`Sleep Results => Target: ${target.name} | HP: ${targetHpValue} | HP Pool: ${remainingSleepHp - targetHpValue} | Status: Missed`);
    sleepTarget.push(`<div class="midi-qol-flex-container"><div>misses</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${targetImg}" width="30" height="30" style="border:0px"></div></div>`);
  }
}
await DDBImporter?.EffectHelper.wait(500);
const sleptResults = `<div><div class="midi-qol-nobox">${sleepTarget.join('')}</div></div>`;
const chatMessage = game.messages.get(args[0].itemCardId);
let content = foundry.utils.duplicate(chatMessage.content);
const searchString = /<div class="midi-qol-hits-display">[\s\S]*<div class="end-midi-qol-hits-display">/g;
const replaceString = `<div class="midi-qol-hits-display"><div class="end-midi-qol-hits-display">${sleptResults}`;
content = await content.replace(searchString, replaceString);
await chatMessage.update({ content: content });

if(args[0].macroPass === "postSave" && workflow.failedSaves) {
    let targets = workflow.failedSaves;

    await foundry.applications.api.DialogV2.wait({
        window: { title: '🗣Command🗣' },
        content: `
            <div class="gps-dialog-container">
                <div class="gps-dialog-section">
                    <div class="gps-dialog-content">
                        <p class="gps-dialog-paragraph">What would you like to command?</p>
                        <div>
                            <div class="gps-dialog-flex">
                                <div style='width: 100%; text-align: center;'>
                                    <input type="text" name="wordInput" id="wordInput" maxlength = "10" autofocus></input>
                                </div>
                                <div id="image-container" class="gps-dialog-image-container">
                                    <img src="${macroItem.img}" class="gps-dialog-image">
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        `,
        buttons: [{
            action: "Cast",
            label: "Cast",
            callback: async (event, button, dialog) => {
                let wordInput = document.querySelector('#wordInput').value;
                for (let target of targets) {
                    await MidiQOL.socket().executeAsGM('_gmSetFlag', { actorUuid : target.actor.uuid, base : 'gambits-premades', key : 'commandWord', value : wordInput })
                }
            }
        }],
        close: async (event, dialog) => {
            return;
        }, rejectClose:false
    });
}
else if(args[0] === "off") {
    let wordInput = await actor.getFlag("gambits-premades", "commandWord")
    let content = `${actor.name} must now ${wordInput}.`;
    let actorPlayer = MidiQOL.playerForActor(actor);
    let chatData = {
        user: actorPlayer.id,
        speaker: ChatMessage.getSpeaker({ token: token }),
        content: content
    };
    ChatMessage.create(chatData);

    const style = {
        "fill": "#ffffff",
        "fontFamily": "Helvetica",
        "fontSize": 50,
        "strokeThickness": 0,
        fontWeight: "bold"
    }

    let delay = 0;
    const delayIncrement = 500;

    wordInput.split('').forEach((char, index) => {
        new Sequence()
            .effect()
            .atLocation(token, {
                offset: { x: index * 0.35 - (wordInput.length - 1) * 0.18, y: -0.7 * token.document.height },
                randomOffset: 0.1,
                gridUnits: true
            })
            .text(char, style)
            .delay(delay)
            .duration(6000)
            .fadeOut(1500)
            .animateProperty("sprite", "position.y", {
                from: -1.5 * token.document.height,
                to: -0.2 * token.document.height,
                duration: 2000,
                gridUnits: true,
                ease: "easeInExpo"
            })
            .scaleIn(0, 1500, { ease: "easeOutElastic" })
            .filter("Glow", { color: 0x4169E1 })
            .zIndex(100 + index)
            .play();

        delay += delayIncrement;
    });

    MidiQOL.socket().executeAsGM('_gmUnsetFlag', { actorUuid : actor.uuid, base : 'gambits-premades', key : 'commandWord' })
}

if (args[0] === "onUpdateActor" && args[1].updates.system.attributes.hp.temp <= 0)
{
  const effectId = args[1].actor.effects.find((e) => e.name === "Motivational Speech").id;
  await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: args[1].actorUuid, effects: [effectId] });
}

if(args[0].macroPass === "isHit")
{
const lastArg = args[args.length - 1];
const effectName = "Motivational Speech Advantage";
const effectCheck = lastArg.options.actor.effects.find((e) => e.name === effectName);
if(!effectCheck)
{
const uuid = lastArg.options.actor.uuid;
let effectData = [{
	disabled: false,
	flags: {
		dae: { specialDuration: ["1Attack"] }
	},
	icon: "icons/svg/clockwork.svg",
	name: "Motivational Speech Advantage",
	changes: [
        {
          key: "flags.midi-qol.advantage.attack.all",
          mode: 2,
          value: "1",
          priority: 20
        }
      ]
	}];
await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: uuid, effects: effectData });
}
}

if(args[0].macroPass === "preActiveEffects")
{
const spellLevel = workflow.itemLevel - 2;
const tempHpAmount = parseInt(spellLevel) * 5;
await Promise.all(workflow.targets.map(target => target.actor.applyTempHP(tempHpAmount)));
}

if(args[0] === "off")
{
	await actor.update({"system.attributes.hp.temp": 0});
}

if (args[0] === "onUpdateActor" && args[1].updates.system.attributes.hp.temp <= 0)
{
  const effectId = args[1].actor.appliedEffects.find(e => e.name === "Motivational Speech").id;
  await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: args[1].actorUuid, effects: [effectId] });
}

if(args[0].macroPass === "isHit")
{
  const effectName = "Motivational Speech Advantage";
  const effectCheck = token.actor.appliedEffects.some(e => e.name === effectName);

  if(!effectCheck)
  {
    const uuid = actor.uuid;
    let effectData = [{
      disabled: false,
      flags: {
        dae: { specialDuration: ["1Attack"] }
      },
      icon: macroItem.img,
      name: "Motivational Speech Advantage",
      changes: [
            {
              key: "flags.midi-qol.advantage.attack.all",
              mode: 2,
              value: "1",
              priority: 20
            }
          ]
      }];
    await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: uuid, effects: effectData });
  }
}

if(args[0].macroPass === "preActiveEffects")
{
  const spellLevel = workflow.itemLevel - 2;
  const tempHpAmount = parseInt(spellLevel) * 5;
  await Promise.all(workflow.targets.map(target => target.actor.applyTempHP(tempHpAmount)));
}

if(args[0] === "off")
{
	await actor.update({"system.attributes.hp.temp": 0});
}
